import React, { Fragment } from 'react';
import styled from 'styled-components';
import { PosterListingPage1 } from '../Constants/PosterListingPage1';

import { sm } from '../../styled-components/device';
import PosterHeader from './PosterHeader';

const PosterCardWrapper = styled.div`
  margin-top: 36px;

  .poster-card {
    margin-bottom: 24px;
    cursor: pointer;

    ${sm} {
      &:hover {
        box-shadow: rgba(0, 0, 0, 0.16) 0px 0px 27px;
      }
    }

    img {
      width: 100%;
      height: 136px;
      object-fit: cover;
      margin-bottom: 12px;
    }

    p {
      color: #CCCCCC;
      margin-bottom: 0;
    }
  }
`;

const PosterCard = () => {
  return (
    <Fragment>
      <PosterHeader title={PosterListingPage1.title} />

      <div className="container xs-full-width">
        <PosterCardWrapper className="row">
          {PosterListingPage1.content && PosterListingPage1.content.map((item, index) => {
            return (
              <div className="col-4" key={index}>
                <div
                  className="poster-card"
                  onClick={() => { alert(`Click Card ${index}`) }}
                >
                  <img src={item["poster-image"]} alt="" />
                  {item.name ? <p>{item.name}</p> : null}
                </div>
              </div>
            )
          })}
        </PosterCardWrapper>
      </div>
    </Fragment>
  );
};

export default PosterCard;
