import React from 'react';
import styled from 'styled-components';

const PosterCardWrapper = styled.div`
  .poster-card {
    margin-bottom: 24px;

    img {
      width: 100%;
      object-fit: cover;
      margin-bottom: 16px;
    }

    p {
      margin-bottom: 0;
    }
  }
`;

const PosterCard = () => {
  return (
    <PosterCardWrapper className="row">
      <div className="col-4">
        <div className="poster-card">
          <img src="./images/poster/poster1.jpg" alt="" />
          <p>The Birds</p>
        </div>
      </div>
    </PosterCardWrapper>
  );
};

export default PosterCard;
