import React from 'react';
import styled from 'styled-components';
import { PosterListingPage1 } from '../Constants/PosterListingPage1';

import { sm } from '../../styled-components/device';

const PosterCardWrapper = styled.div`
  .poster-card {
    margin-bottom: 24px;
    cursor: pointer;

    ${sm} {

    }

    img {
      width: 100%;
      height: 136px;
      object-fit: cover;
      margin-bottom: 12px;
    }

    p {
      color: #FFFFFF;
      margin-bottom: 0;
    }
  }
`;

const PosterCard = () => {
  return (
    <PosterCardWrapper className="row">
      {PosterListingPage1 && PosterListingPage1.map((item, index) => {
        return (
          <div className="col-4" key={index}>
            <div className="poster-card">
              <img src={item["poster-image"]} alt="" />
              {item.name ? <p>{item.name}</p> : null}
            </div>
          </div>
        )
      })}
    </PosterCardWrapper>
  );
};

export default PosterCard;
